        publishs.put(TRASH, new Publish(TRASH));
        publishs.put(GATHERING, new Publish(GATHERING));

        Iterator<Publish> pIterator = publishs.values().iterator();

        while (pIterator.hasNext()) {
            Publish p = pIterator.next();
            p.criarTopico();
        }


