            if (valoresP[2].equals("TRAFEGO_ON")) {
                cidade.attFluxoVeiculos(Integer.parseInt(valoresP[0]), valoresP[2]);
            } else if (valoresP[2].equals("TRAFEGO_OFF")) {
                cidade.attFluxoVeiculos(Integer.parseInt(valoresP[0]), valoresP[2]);
            } else {
                cidade.attNivelPol(Integer.parseInt(valoresP[0]), valoresP[2]);
            }
            //Publisher excession
            Publish p = publishs.get(POLLUTION);
            if (p != null) {
                p.publicar(context);
            } else {
                System.out.println("não existe publish associado a " + POLLUTION);
            }

