            } else if (valoresP[2].equals("OUTRO")) {
                cidade.attChuvaAcida(Integer.parseInt(valoresP[0]), valoresP[2], valoresP[3]);
            } else if (valoresP[2].equals("ACID_OFF")) {
                cidade.attChuvaAcida(Integer.parseInt(valoresP[0]), valoresP[2], null);
